import "../Pagination2/App.css"
import React, { useEffect, useState } from 'react'

function App() {
    const url = "https://dummyjson.com/products?limit=100"
    const [products, setProducts] = useState([]);
    const [page, setPage] = useState(1)
    const [totalPages, setTotalPages] = useState();

    const fetchProducts = async () => {

        const res = await fetch(url)
        const data = await res.json()
        // if (data && data.products)
            setProducts(data.products)
            setTotalPages(data.total)
        // console.log(data)
    };
    useEffect(() => {
        fetchProducts()
    }, [])

    console.log(products)

    const handlePageChange = (SPage) => {
        if(
            SPage>=1 && 
            SPage<=products.length/10 && 
            SPage!==page) 
        setPage(SPage)
    }
    return (
        <div>
            
            {
            products.length>0 &&(
            <div className='products'>
                {
                    products.slice(page*10-10, page*10).map((prod) => {
                      return(
                         <div className="products__single" key={prod.title}>
                             <img className='' alt={prod.title}  src={prod.thumbnail}/>
                             <span>{prod.id}.{prod.title}<br/>Price: ${prod.price} </span>
                    
                             </div>

                      )
                    })
                }
            </div>
          
           ) }
           {
            products.length>0 &&<div className="pagination d-flex justify-content-center">
                <span className=" mx-1 btn btn-sm" id={page>1?'':'pagination_desabled'} onClick={() => handlePageChange(page-1) }>◀️</span>
                {
                    [...Array(products.length/10)].map((_, i) => {
                        return(
                            <div className="btn btn-sm border-dark "><span className={page===i+1?' bg-primary':''} key={i} onClick={() => handlePageChange(i+1)}>{i+1}</span> </div>
                        )
                    })
                }
                <span className=" btn btn-sm mx-1" id={page<products.length/10?'':'pagination_desabled'} onClick={() => handlePageChange(page+1)}>▶️</span>
            </div>
           }
        </div>
    )
}

export default App
