import React, { useEffect, useState, useParams } from 'react'

function ContactDetails() {
    const{empid}=useParams();
    const [empData, setEmpData]=useState({});

    
    useEffect(()=>{
        fetch('http://localhost:8000/contacts/'+empid)
        .then((res)=>{
            return res.json();
        })
        .then((resp)=>{
            setEmpData(resp);
        }).catch
    })
  return (
    <div>{ empData &&
    <h4> The Contact Details : {empData.name} ({empData.id})</h4>
    
}
    </div>
  )
}

export default ContactDetails
