import React from 'react'

function LuckyNumber() {
// var n = prompt("Enter a number");
var sum = 0;
while (n > 0) {
    const rem = n % 10;
    sum += rem;
    n = parseInt(n/10);
    if (n == 0 && sum >= 10) {
        n = sum;
        sum = 0;
        
    }
}




  return (
    <div>
      {sum}
    </div>
  )
}

export default LuckyNumber
