import React from 'react'

function LuckyNumber() {
    const HandleNumber = (e) => {
        var n = e.target.value
        var sum = 0;
        
        while (n > 0) {
            const rem = n % 10;
            sum += rem;
            n = parseInt(n/10);
            if (n == 0 && sum >= 10) {
                n = sum;
                sum = 0;
            }
        }
        console.log(sum);
    }





  return (
    <div>
        <dt>Enter a number</dt>
        <dd><input type="number" /></dd>
        <button onClick={HandleNumber}> Submit</button>
        <br />
      
    </div>
  )
}

export default LuckyNumber
