import React from 'react'
import { Link } from 'react-router-dom';

function ContactCreate() {

    const [id, setId] = useState(0);
    const [name, setName] = useState('');
    const [email, setEmail] = useState('');
    const [phone, setPhone] = useState(0);
    const [isActive, setIsActive] = useState(true);
    const CreateContact = (e) => {

        
        alert("Contact Created Successfully"); 
    }
    return (
        <div className='container-fluid mt-1'>
            <h4>Create New Contact</h4>
            <dl className=" w-50">
                <dt>ID</dt>
                <dd><input className='form-control mt-2 w-50' type="number" /></dd>
                <dt>Name</dt>
                <dd><input className='form-control mt-2 w-50' type="text" /></dd>
                <dt>Email</dt>
                <dd><input className='form-control mt-2 w-50' type="email" /></dd>
                <dt>Mobile</dt>
                <dd><input className='form-control mt-2 w-50' type="number" /></dd>
                <dt>Is Active</dt>
                <dd className='form-switch'><input className='form-check-input mt-2'  type="checkbox" role='switch' /></dd>
                <button className='mt-2 w-25 btn btn-primary' type="submit" onClick={CreateContact}>Submit</button>
                <Link to='/' className='mt-2 mx-2 btn btn-danger'>Cancel</Link>
            </dl>
            
        </div>


    )
}

export default ContactCreate
