import "../Pagination2/App.css"
import React, { useEffect, useState } from 'react'

function App() {
    const url = "https://dummyjson.com/products?limit=100"
    const [products, setProducts] = useState([]);
    const [page, setPage] = useState(1)

    const fetchProducts = async () => {

        const res = await fetch(url)
        const data = await res.json()
        // if (data && data.products)
            setProducts(data.products)
        // console.log(data)
    };
    useEffect(() => {
        fetchProducts()
    }, [])

    console.log(products)
    return (
        <div>
            
            {
            products.length>0 &&(
            <div className='products'>
                {
                    products.slice(page, page*12).map((prod) => {
                      return(
                         <div className="products__single" key={prod.title}>
                             <img className='' alt={prod.title}  src={prod.thumbnail}/>
                             <span>{prod.id}.{prod.title}<br/>Price: ${prod.price} </span>
                    
                             </div>

                      )
                    })
                }
            </div>
          
           ) }
        </div>
    )
}

export default App
