import React, { useEffect, useState } from 'react'

function EmpListing() {
    const [empData, setEmpData] = useState(null);

    useEffect(() => {
        fetch('http://localhost:8000/contacts')
        .then((res) => {
            return res.json();
        }).then((resp)=>{
            setEmpData(resp);
        }).catch((err)=>{
            console.log(err.message);
        })
        },[])
    return (
        <div className='container-fluid'>
            <div className='card'>
                <div className='card-title'>
                    <h3>Employee Listing</h3>
                </div>
                <div className='card-body'>
                    <table className='table table-bordered'>
                        <thead className='bg-dark text-primary'>
                            <tr>
                                <td>ID</td>
                                <td>Name</td> 
                                <td>Email</td>
                                <td>Phone</td>
                                <td>Action</td>
                            </tr>
                        </thead>
                        <tbody>
                            {
                                empData &&
                                empData.map(item=>(
                                    <tr key={item.id}>
                                        <td>{item.id}</td>
                                        <td>{item.name}</td>
                                        <td>{item.email}</td>
                                        <td>{item.phone}</td>
                                        <td><a className='btn btn-sm btn-warning'>Edit</a>
                                        <a className='btn btn-sm btn-danger'>Delete</a>
                                        <a className='btn btn-sm btn-primary'>Details</a></td>
                                    </tr>
                                ))
                            }
                        </tbody>
                    </table>

                </div>
            </div>

        </div>
    )
}

export default EmpListing
