import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import ContactCreate from "./ContactCreate";
function ContactListing() {
  const [empData, setEmpData] = useState(null);

  useEffect(() => {
    fetch("http://localhost:8000/contacts")
      .then((res) => {
        return res.json();
      })
      .then((resp) => {
        setEmpData(resp);
      })
      .catch((err) => {
        console.log(err.message);
      });
  }, []);
  return (
    <div className="container-fluid">
      <div className="card">
        <div className="card-title app">
          <h3>Employee Listing</h3>
        </div>
        <div className="card-body">
          <div>
            <Link
              to="contacts/create"
              className="btn btn-success bi bi-plus-circle mb-2"
            >
              {" "}
              Add New
            </Link>
          </div>
          <table className="table table-bordered">
            <thead className="bg-dark text-primary">
              <tr>
                <td>ID</td>
                <td>Name</td>
                <td>Email</td>
                <td>Phone</td>
                <td>Action</td>
              </tr>
            </thead>
            <tbody>
              {empData &&
                empData.map((item) => (
                  <tr key={item.id}>
                    <td>{item.id}</td>
                    <td>{item.name}</td>
                    <td>{item.email}</td>
                    <td>{item.phone}</td>
                    <td>
                      <a
                        onClick={() => {
                          LoadDetails(item.id);
                        }}
                        className="btn btn-sm btn-primary bi bi-eye me-2"
                      ></a>
                      <a
                        onClick={() => {
                          EditDetails(item.id);
                        }}
                        className="btn btn-sm btn-warning bi bi-pen me-2"
                      ></a>
                      <a className="btn btn-sm btn-danger bi bi-trash "></a>
                    </td>
                  </tr>
                ))}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
}

export default ContactListing;
