import "../Pagination2/App.css"
import React, { useEffect, useState } from 'react'

function App() {
    const url = "https://dummyjson.com/products?limit=100"
    const [products, setProducts] = useState([]);

    const fetchProducts = async () => {

        const res = await fetch(url)
        const data = await res.json()
        // if (data && data.products)
            setProducts(data.products)
        // console.log(data)
    };
    useEffect(() => {
        fetchProducts()
    }, [])

    console.log(products)
    return (
        <div>
            
            {
            products.length>0 &&(
            <div className='products'>
                {
                    products.map((prod) => {
                      return(
                         <div cl key={prod.title}>
                             <span>{prod.id}</span>
                             <img className='card mt-2 ' alt={prod.title} style={{width:"400px",height:"200px"}} src={prod.thumbnail}/>
                              Brand- {prod.brand }<br/>Model- {prod.title}<br/>Description- {prod.description}<br/> Stock Available- {prod.stock}<br/>Price: ${prod.price}----
                             </div>

                      )
                    })
                }
            </div>
          
           ) }
        </div>
    )
}

export default App
